// filter默认的有以下规则：
/**
	lowercase: 转换为小写
	uppercase: 转换为大写
	trim: 去除左右空白
	callback:
	this=Schema
	function(value, respond){
	    respond(value + '123');
	}
*/

// 假设用户表有以下两个字段
// email
// nickname



// 邮箱转换为小写
filter.add('email', 'lowercase');


// 昵称
filter.add('nickname', function(value, respond) {
    if (value === undefined) value = '     匿名用户' + Date.now() + '      ';
    respond(value);
});
filter.add('nickname', 'trim');
